CREATE TABLE  `qfacture_db`.`template` (
  `Id` smallint(5) unsigned NOT NULL AUTO_INCREMENT COMMENT 'Id template',
  `Type` varchar(10) NOT NULL COMMENT 'Type template',
  `Sel` varchar(1) NOT NULL COMMENT 'Template sélectionné (pour le type)',
  `Name` varchar(255) NOT NULL COMMENT 'Désignation template',
  `Content` text NOT NULL COMMENT 'Contenu du template',
  PRIMARY KEY (`Id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='Table des templates de facture';

INSERT INTO `qfacture_db`.`template` VALUES
(1, 'html', 'x', 'Thème par défaut', '<html>\r\n<head>\r\n	<style type="text/css">\r\n	/* GENERAL --------------------------------------------------------*/\r\n\r\n	* {\r\n		margin: 0;\r\n		padding: 0;\r\n	}\r\n\r\n	body {\r\n		background: #fff;\r\n		color: #393939;\r\n		font-family: Arial;\r\n	}\r\n\r\n	img {\r\n		border: 0;\r\n	}\r\n\r\n	p {\r\n		margin: 0px;\r\n		padding: 0px;\r\n	}\r\n\r\n	/** Logo **/\r\n\r\n	#page-top-outer {\r\n		background: url(images/top_bg.jpg) top center repeat-x;\r\n		border-bottom: 1px solid #7e7e7e;\r\n		height: 92px;\r\n	}\r\n\r\n	#logo {\r\n		float: left;\r\n		margin: 45px 0 0 45px;\r\n	}\r\n\r\n	#logo h1 {\r\n		color: white;\r\n		font-family: Arial, Helvetica, sans-serif;\r\n		font-size: 30px;\r\n	}\r\n\r\n	/** Contenu **/\r\n\r\n	#content h1 {\r\n		margin-bottom: 10px;\r\n	}\r\n\r\n	#content {\r\n		color: #333;\r\n		font-family: Arial, Helvetica, sans-serif;\r\n		font-size: 12px;\r\n		line-height: 18px;\r\n		padding-top: 2em;\r\n		padding-left: 4em;\r\n		padding-right: 4em;\r\n	}\r\n\r\n	/** Tableaux **/\r\n\r\n	table {\r\n		border-collapse: collapse;\r\n		margin: 10px 0;\r\n	}\r\n\r\n	th {\r\n		height: 50px;\r\n		padding-left: 10px;\r\n		padding-right: 45px;\r\n	}\r\n\r\n	td {\r\n		border: 1px solid #D2D2D2;\r\n		padding: 10px;\r\n	}\r\n\r\n	/* Détails d''une facture */\r\n\r\n	.align_left {\r\n		text-align: left;\r\n	}\r\n\r\n	#invoice_mentions {\r\n		margin-top: 20px;\r\n	}\r\n\r\n	#invoice_right_part {\r\n		text-align: left;\r\n		float: right;\r\n		margin-top: -120px;\r\n	}\r\n\r\n	#invoice_customer_name  {\r\n		font-weight: bold;\r\n	}\r\n\r\n	#invoice_products {\r\n		text-align: right;\r\n		width: 100%;\r\n		margin-top: 30px;\r\n	}\r\n\r\n	#invoice_products tfoot td {\r\n		border: none;\r\n	}\r\n	\r\n	#invoice_compagny_infos {\r\n		text-align: center;\r\n	}\r\n	</style>\r\n</head>\r\n<body>\r\n<div id="content">\r\n	<div id="invoice_compagny_logo"><img src="{% logo_url %}" alt="" /></div>\r\n\r\n	<p id="invoice_compagny_adress">\r\n		{% ae_name %}<br />\r\n		{% ae_address %}<br />\r\n		{% ae_zip %} {% ae_city %}\r\n	</p>\r\n	\r\n\r\n	<p id="invoice_mentions">\r\n		N° SIREN : {% siren %}<br />\r\n		Dispensé d’immatriculation au registre<br />\r\n		du commerce et des sociétés (RCS) et<br />\r\n		au répertoire des métiers (RM)\r\n	</p>\r\n	\r\n	<div id="invoice_right_part">\r\n		<p id="invoice_customer">\r\n			<span id="invoice_customer_name">{% customer_name %}</span><br />\r\n			{% customer_address %} {% customer_address2 %}<br />\r\n			{% customer_zip %} {% customer_city %}\r\n		</p>\r\n\r\n		<p id="invoice_date">\r\n			{% invoice_date %}\r\n		</p>\r\n	</div>\r\n\r\n	<span id="invoice_number">Facture n°{% ref %}</span>\r\n	\r\n	<p id="invoice_desc">\r\n		{% invoice_comment %}\r\n	</p>\r\n	\r\n	<table id="invoice_products">\r\n		<thead>\r\n			<tr>\r\n				<th class="align_left">Désignation</th>\r\n				<th>Prix unitaire</th>\r\n				<th>Quantité</th>\r\n				<th>Montant (EUR)</th>\r\n			</tr>\r\n		</thead>\r\n\r\n		<tbody>\r\n			{% product_line %}\r\n			<tr>\r\n				<td class="align_left">{% designation %}</td>\r\n				<td>{% prix_unitaire %}</td>\r\n				<td>{% quantite %}</td>\r\n				<td>{% montant %}</td>\r\n			</tr>\r\n			{% product_line %}\r\n		</tbody>\r\n\r\n		<tfoot>\r\n			<tr>\r\n				<td class="align_left" colspan="2">Total HT</td>\r\n				<td colspan="2">{% invoice_amount %}</td>\r\n			</tr>\r\n			<tr>\r\n				<td class="align_left" colspan="3">TVA non applicable, art. 293 B du CGI</td>\r\n			</tr>\r\n		</tfoot>\r\n	</table>\r\n\r\n	<p>\r\n		À régler par chèque ou par virement bancaire :\r\n	</p>\r\n\r\n	<table>\r\n		<tbody>\r\n			<tr>\r\n				<td>Code banque</td>\r\n				<td>Code guichet</td>\r\n				<td>N° de compte</td>\r\n				<td>Clé RIB</td>\r\n			</tr>\r\n			<tr>\r\n				<td>12222</td>\r\n				<td>22222</td>\r\n				<td>22222222222</td>\r\n				<td>22</td>\r\n			</tr>\r\n			<tr>\r\n				<td>IBAN N°</td>\r\n				<td colspan="3">FR22 2222 2222 2222 2222 2222 222</td>\r\n			</tr>\r\n			<tr>\r\n				<td>Code BIC</td>\r\n				<td colspan="3">Code bic</td>\r\n			</tr>\r\n		</tbody>\r\n	</table>\r\n\r\n	<p id="invoice_compagny_infos">\r\n		{% ae_name %} – {% ae_address %} – {% ae_zip %} {% ae_city %}<br />\r\n		Telephone : {% ae_tel %} – Site Internet : {% ae_web %} – E-mail : {% ae_mail %}<br />\r\n		N° SIREN : {% siren %} – Code APE 6201Z\r\n	</p>\r\n</div>\r\n</body>\r\n</html>');
